import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, CardMedia, Typography, CardHeader } from '@material-ui/core';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

const useStyles = makeStyles({
  root: {
    minHeight: 100,
    minWidth: 100,
    maxWidth: 200,
    maxHeight: 200,
  },
  displayText: {
    visibility: 'hidden',
    textAlign: 'center',
    background: '#fff',
    opacity: '0.8',
  },
  cardHover: {
    position: 'relative',
    overflow: 'hidden',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    '&:hover': {
      '& p': {
        visibility: 'visible',
      }
    }
  }
});

export default function MediaCard(props) {
  const classes = useStyles();
  const {name, designation, linkedin, image}=props;
  return (
    <Card className = {classes.root}>
      <CardContent className = {classes.cardHover} style={{ backgroundImage: `url(${image})`}}>
        <Typography className={classes.displayText}>
          <h4>{name}</h4>
          <body>{designation}</body>
        </Typography>
      </CardContent>
    </Card>
  );
}
