import React from 'react';
import { NavLink } from 'react-router-dom';
import { Button, Grid, makeStyles, TextField, Typography } from '@material-ui/core';
import { Facebook, Instagram, LinkedIn } from '@material-ui/icons';
import navopts from '../Navigation_Data.json';

const useStyles = makeStyles((theme) => ({
    root: {
        background: 'rgb(17, 89, 122)',
    },
    logo: {
        color: 'rgb(214, 190, 207)',
        textDecoration: 'none',
        display: 'flex',
        flexDirection: 'column',
        fontFamily: 'Verdana',
        fontWeight: 'bold',
        fontSize: '20px',
        paddingTop: '25px',
        paddingLeft: '50px',
        paddingBottom: '10px',
    },
    links: {
        color: 'rgb(214, 190, 207)',
        textDecoration: 'none',
        fontFamily: 'Verdana',
        fontSize: '10px',
        paddingLeft: '50px',
    },
    icons: {
        paddingTop: '20px',
        paddingLeft: '50px',
        paddingBottom: '30px',
    },
    newsletter_head: {
        fontSize: '20px',
        color: 'white',
        textAlign: 'right',
        paddingTop: '25px',
        paddingRight: '50px',
    },
    input: {
        marginRight: '20px',
        marginTop: '0px',
        color: 'white',
        backgroundColor: 'white',
    },
}));

function Footer() {
    const classes = useStyles();
    return(
        <Grid container className={classes.root}>
                <Grid item xs={6}>
                    <Typography className={classes.logo}>Becoming<br/>Businesswomen</Typography>
                    
                    {navopts.footer.map((e) => {
                        return (
                            <>
                            <NavLink className={classes.links} activeClassName="active" to={e.to} exact>{e.label}</NavLink><br/>
                            </>
                        )
                    })}

                    <div className={classes.icons}>
                        <a style={{color: 'white'}} href={navopts.social_media_icons[0].link}><Facebook/></a>
                        <a style={{color: 'white'}} href={navopts.social_media_icons[1].link}><Instagram/></a>
                        <a style={{color: 'white'}} href={navopts.social_media_icons[2].link}><LinkedIn/></a>
                    </div>
                </Grid>
                <Grid container item xs={6}>
                    <Grid item xs={12}>
                    <Typography className={classes.newsletter_head} variant="body2">Join Our Mailing List!</Typography>
                    </Grid>
                    <Grid item xs={8}>
                        <TextField
                        id="email"
                        label="Email"
                        variant="filled"
                        margin="dense"
                        className={classes.input}
                        //value={this.state.email}
                        //onChange={(e) => this.setState({ message: e.target.value })}
                        type="text"
                        fullWidth/>
                        </Grid>
                        <Grid item xs={4}>
                    <Button>Join</Button>
                    </Grid>
                    <Grid item xs={12}>
                    <Typography variant="body2">Created by B.B. Tech Team</Typography>
                    </Grid>
                </Grid>
            </Grid>
    )
}

export default Footer;
