import React from 'react';
import '../../styles/Sidebar.css';

const sideDrawer = props => {   
    let drawerClasses = 'side-drawer';
    if(props.show) {
        drawerClasses = 'side-drawer open';
    }

    return (
    <nav className = {drawerClasses}>
        <ul>
            <li>
                <NavLink to="/" exact>
                    HOME
                </NavLink>                    
            </li>
            <li>
                <NavLink to="/about">
                    ABOUT
                </NavLink>
            </li>
            <li>
                <NavLink to="/our-team">
                    OUR TEAM
                </NavLink>
            </li>
            <li>
                <NavLink to="/join">
                    JOIN US!
                </NavLink>
            </li>
            <li>
                <NavLink to="/contact">
                    CONTACT
                </NavLink>
            </li>
        </ul> 
    </nav>
    );
};

export default sideDrawer;
