import { Button, Grid, Typography, makeStyles, Paper } from '@material-ui/core';
import React from 'react';
import NavigationBar from '../components/NavigationBar.js';
import '../styles/Home.css';
import Footer from '../components/Footer.js';
import Header from '../images/home_header.jpg';

const useStyles = makeStyles((theme) => ({
    content: {
        width: '100%',
        height: '100%',
        top: 0,
        position: 'absolute',
        textAlign: 'center',
        background: 'rgba(0,0,0,0.3)',
    },
    name: {
        paddingTop: '15%',
        fontWeight: 'bolder',
        color: 'white',
    },
    joinus: {
        background: '#fc9fb6',
        fontSize: '25px',
        color: 'white',
        fontWeight: 'bold',
        width: '20%',
        marginTop: '5%',
        borderRadius: '0px',
        borderColor: 'white',
        borderStyle: 'solid',
        borderWidth: '2px',
        "&:hover": {
            background: '#fc9fb6',
        }
    }
}));

const Home = () => {
    const classes = useStyles();
    return (
        <>
        <NavigationBar/>

        <div style={{position: 'relative'}}>
            <img width='100%' src={Header} style={{opacity: 0.7, height: '100%'}}/>
            <div className={classes.content}>
                <Typography variant="h1" className={classes.name}>
                    Becoming<br/>Businesswomen
                </Typography>
                <Button className={classes.joinus}>Join Us</Button>
            </div>
        </div>

        <div style={{height: '90vh', background: 'rgb(17, 89, 122)'}}>
            <Typography variant="h3" style={{paddingTop: '10%', color: 'white', textAlign: 'center'}}>
                Who Are We?
            </Typography>
            <Paper style={{height: '25%', background: 'white', marginLeft: '20%', marginRight: '20%', padding: '5%'}}>
                Becoming Businesswomen is a student-run organization dedicated to establishing a community of undergraduate women passionate about business and supporting them through education, mentorship and collaboration. Through B.B., you will have access to a growing community of women from schools all over the world, as well as resources such as guides, videos and webinars.
            </Paper>
        </div>
        vnhogihr
        <Footer/>
        </>
    )
}

export default Home
