import React from 'react';
import { Button, Typography, makeStyles, Card, CardContent, CardMedia, ButtonBase } from '@material-ui/core';
import Emily from '../../images/sample_image.jpg';

const useStyles = makeStyles({
  root: {
    maxWidth: 220,
    maxHeight: 220,
    backgroundSize: '50%',
    backgroundRepeat: 'no-repeat',
  },
  desgn: {
    color: 'pink',
    visibility: 'hidden',
    textAlign: 'center',
  },
  name: {
    color: 'pink',
    visibility: 'hidden',
    textAlign: 'center',
  },
  learn: {
    color: 'pink',
    visibility: 'hidden',
  },
  cardAction: {
    display: 'block',
    height: '100%',
    width: '100%',
    '&:active, &:focus': {
      backgroundColor: 'rgb(22, 49, 90)',
        '& h5': {
          visibility: 'visible',
        },
        '& h6': {
          visibility: 'visible',
        },
        '& Button': {
          visibility: 'visible',
        }
    }
  },
  content: {
    height: 220,
    position: 'relative',
    overflow: 'hidden',
    "&:hover": {
        backgroundColor: 'rgb(22, 49, 90)',
        transition: 'all 0.5s ease',
        '& h5': {
          visibility: 'visible',
          transition: 'all 0.7s ease',
        },
        '& h6': {
          visibility: 'visible',
          transition: 'all 0.7s ease',
        },
        '& Button': {
          visibility: 'visible',
          transition: 'all 0.7s ease',
        }
    },
  },
});

export default function MediaCard(props) {
  const classes = useStyles();
  const {name, designation, image}=props;
  return (
    <Card style={{ background: `url(${image})` }} className={classes.root}>
      <ButtonBase focusRipple className={classes.cardAction}>
      <CardContent className={classes.content}>
        <Typography variant="h5" className={classes.name}>{name}</Typography>
        <Typography variant="h6" className={classes.desgn}>{designation}</Typography>
        <Button className={classes.learn}>Learn More</Button>
      </CardContent>
      </ButtonBase>
    </Card>
  );
}
