import { Button, makeStyles, Paper, Typography } from '@material-ui/core';
import React from 'react';
import FacebookIcon from '@material-ui/icons/Facebook';
import { PersonAdd } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
    root: {
        height: '200px',
        background: 'rgb(235, 197, 216)',
        marginBottom: '20%',
    }
}));

const JoinCard = (props) => {
    const classes = useStyles();
    const { icon, head, desc, buttonText } = props;
    
    return(
        <>
        <Paper className={classes.root} square>
            <FacebookIcon color="primary" fontSize="large" style={icon === '0' ? {visibility: 'gone'} : {}}/> 
            <PersonAdd color="primary" fontSize="large" style={icon === '1' ? {visibility: 'gone'} : {}}/>
            <Typography variant="h4">
                {head} === 1 ? Join Our Facebook Group : Become a Member
            </Typography>
            <Typography variant="body2">
                {desc} === 1 
                    ? After becoming a member, hop over to our Facebook group â€‹to introduce yourself and ask questions! 
                    : Sign up here to become a general member by filling out our google form below:
            </Typography>
            <Button>
                {buttonText} === 1 ? Facebook Group : Membership Signup
            </Button>
        </Paper>
        </>
    )
}

export default JoinCard
