import React from 'react';
import './Navbar.css';

const Sidebar = (props) => {
  return(
    <nav id="sidebar">
      <div className="brand">{props.brand}</div>
      <ul>
        <li><a href="#nowhere">HOME</a></li>
        <li><a href="#nowhere">OUR TEAM</a></li>
        <li><a href="#nowhere">JOIN US!</a></li>
        <li><a href="#nowhere">CONTACT</a></li>
      </ul>
    </nav>
  );
}

const ContentContainer = (props) => {
  return ( <div className="content-container">{ props.children }</div> );
}

const Overlay = ({ toggleSidebar }) => {
  return (
    <div className="content-container-overlay" onClick={toggleSidebar}></div>
  );
}

const SidebarToggleButton = ({ toggleSidebar }) => {
  return(
    <div className="sidebar-toggle-container">
      <button
        id="sidebar-toggle-button"
        onClick={ toggleSidebar } >
      </button>
    </div>
  );
}

export default class Navbar extends React.Component {
  state = { sidebarIsOpen: false };

  toggleSidebar = () => {
    const body    = document.getElementsByTagName('body')[0];
    const overlay = document.getElementsByClassName('content-container-overlay')[0];

    if (!this.state.sidebarIsOpen) {
      this.setState({ sidebarIsOpen: true });
      body.classList.add("open");
      overlay.style.display = "block";
      setTimeout(() => {overlay.classList.add('active');}, 100);
    } 
    else {
      this.setState({ sidebarIsOpen: false });
      body.classList.remove("open");
      overlay.classList.remove('active');
      setTimeout(() => { overlay.style.display = "none"; }, 350);
    }
  }

  render(){
    return (
      <React.Fragment>
        <Sidebar brand="DC" />

        <ContentContainer>
          <Overlay toggleSidebar={this.toggleSidebar} />
          <SidebarToggleButton toggleSidebar={this.toggleSidebar} />
        </ContentContainer>
      </React.Fragment>
    );
  }
};
