import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardActionArea, CardContent, CardMedia, Typography, IconButton, CardHeader } from '@material-ui/core';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

const useStyles = makeStyles({
  header: {
    paddingBottom: 0,
    marginBottom: 0,
  },
  content: {
    marginTop: 0,
    paddingTop: 0,
    marginBottom: 0,
    paddingBottom: 0,
  }
});

export default function MediaCard(props) {
  const classes = useStyles();
  const { name, designation, linkedin } = props;
  return (
    <Card>
      <CardMedia
        component="img"
        alt="Contemplative Reptile"
        height="200"
        image="../../images/sample_image.jpg"
        title="Contemplative Reptile"
      />
      <CardHeader
        className = {classes.header}
        title = {
          <Typography variant="h6">
            {props.name}
          </Typography>
        }
        action = {
          <IconButton aria-label="connect">
            <LinkedInIcon />
          </IconButton>
        }
      />
      <CardContent className = {classes.content}>
        <Typography variant="body2">
          Director of Banking - Buy-Side
        </Typography>
      </CardContent>
    </Card>
  );
}
