import React, { Component, useState } from 'react';
import { NavLink } from 'react-router-dom';
import '../../styles/Toolbar.css';
import DrawerToggleButton from './DrawerToggleButton';
import { FaCaretDown } from 'react-icons/fa';
import Select from 'react-select';

function Toolbar(props) {

    const data = [
        {
            value: 1,
            className: 'dropdown',
            label: "E-Board"
        },
        {
            value: 2,
            className: 'dropdown',
            label: "Tech Team"
        },
        {
            value: 3,
            className: 'dropdown',
            label: "Ambassadors"
        }
    ];

    const [selectedOption, setSelectedOption] = useState(null); 

    const handleChange = e => {
        setSelectedOption(e);
    }

    const handleClick = (value) => {
        this.props.history.push('/Join');
    }
    
    return(
    <header className="toolbar">
            <nav className = "toolbar_navigation" role="navigation" aria-label="main navigation">
            <div className = "toolbar_toggle-button">
                <DrawerToggleButton click = {props.drawerClickHandler} />
            </div>
            <div className = "toolbar_logo">
                <a href = "/">BECOMING<br/>BUSINESSWOMEN</a>
            </div>
            <div className = "spacer" />
            <div className = "toolbar_navigation-items">
                <ul>
                    <li>
                        <NavLink className="navbar-item" activeClassName="is-active" to="/" exact>
                            HOME
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="navbar-item" activeClassName="is-active" to="/about">
                            ABOUT
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="navbar-item" activeClassName="is-active" to="/our-team">
                            OUR TEAM
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="navbar-item" activeClassName="is-active" to="/join">
                            JOIN US!
                        </NavLink>
                    </li>
                    <li>
                        <NavLink className="navbar-item" activeClassName="is-active" to="/contact">
                            CONTACT
                        </NavLink>
                    </li>
                </ul>
            </div>
        </nav>
        </header>
        );
}

export default Toolbar;
