import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, Button } from '@material-ui/core';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

const useStyles = makeStyles({
  root: {
    height: 220,
    width: 220,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
  },
  displayText: {
    visibility: 'hidden',
    textAlign: 'center',
    height: 190,
    width: 190,
    fontFamily: 'Verdana',
  },
  name: {
    fontWeight: 'normal',
    fontSize: '20px',
    color: '#fff',
    marginTop: 0,
    marginBottom: '6px',
  },
  desc: {
    fontSize: '18px',
    fontWeight: 'lighter',
    color: 'pink',
    marginTop: '0'
  },
  cardHover: {
    position: 'relative',
    top:0,
    '&:hover': {
      backgroundColor: 'rgb(0, 13, 65)',
      '& h3': {
        visibility: 'visible',
      },
      '& p': {
        visibility: 'visible',
      },
      '& a': {
        visibility: 'visible',
      }
    }
  }
});

export default function MediaCard(props) {
  const classes = useStyles();
  const {name, designation, linkedin, image}=props;
  return (
    <Card 
      className={classes.root} 
      style={{ backgroundImage: `url(${image})`}}>
      
      <CardContent className = {classes.cardHover}>
        <div className={classes.displayText}>
          <h3 className={classes.name}>{name}</h3>
          <p className={classes.desc}>{designation}</p>
          <a href="">Learn More</a>
        </div>
      </CardContent>
    
    </Card>
  );
}
