import React from 'react';
import { Button, Typography, makeStyles, Card, CardContent, CardMedia } from '@material-ui/core';
import Emily from '../../images/sample_image.jpg';

const useStyles = makeStyles({
  root: {
    maxWidth: 220,
    maxHeight: 220,
    background: `url(${Emily})`,
    backgroundSize: '100%',
    backgroundRepeat: 'no-repeat',
  },
  desgn: {
    color: 'pink',
    visibility: 'hidden',
    textAlign: 'center',
  },
  name: {
    color: 'pink',
    visibility: 'hidden',
    textAlign: 'center',
  },
  content: {
    height: 220,
    position: 'relative',
    overflow: 'hidden',
    "&:hover": {
      backgroundColor: 'rgb(22, 49, 90)',
      '& h5': {
        visibility: 'visible',
      },
      '& h6': {
        visibility: 'visible',
      }
    }
  },
});

export default function MediaCard(props) {
  const classes = useStyles();
  const {name, designation, image}=props;
  return (
    <Card className={classes.root}>
      <CardContent className={classes.content}>
        <Typography variant="h5" className={classes.name}>{name}</Typography>
        <Typography variant="h6" className={classes.desgn}>{designation}</Typography>
        <Button className={classes.learn}>Learn More</Button>
      </CardContent>
    </Card>
  );
}
