import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, CardMedia, Typography, IconButton, CardHeader } from '@material-ui/core';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

const useStyles = makeStyles({
  root: {
    
  },
  header: {
    margin: 0,
    padding: 0,
    textAlign: 'center',
  },
  media: {
    padding: '10px',
  },
});

export default function MediaCard(props) {
  const classes = useStyles();
  const {name, designation, linkedin, image}=props;
  return (
    <Card className = {classes.root}>
      <CardMedia
        className={classes.media}
        component="img"
        height="200"
        image={image}
        title={name}
      />
      <CardHeader
        className = {classes.header}
        title = {
          <Typography variant="h6">
            {name}
          </Typography>
        }
        subheader = {designation}
        //action = {
          //<IconButton aria-label="connect" onClick={linkedin}>
            //<LinkedInIcon />
          //</IconButton>
        //}
      />
    </Card>
  );
}
