const Sidebar = (props) => {
  return(
    <nav id="sidebar">
      <div className="brand">{props.brand}</div>
      <ul>
        <li><a href="#nowhere">Item 1</a></li>
        <li><a href="#nowhere">Item 2</a></li>
        <li><a href="#nowhere">Item 3</a></li>
      </ul>
    </nav>
  );
}


const ContentContainer = (props) => {
  return ( <div className="content-container">{ props.children }</div> );
}


const Overlay = ({ toggleSidebar }) => {
  return (
    <div className="content-container-overlay" onClick={toggleSidebar}></div>
  );
}


const SidebarToggleButton = ({ toggleSidebar }) => {
  return(
    <div className="sidebar-toggle-container">
      <button
        id="sidebar-toggle-button"
        onClick={ toggleSidebar }
      ></button>
    </div>
  );
}


const RandomContent = () => {
  return (
    <React.Fragment>
      <h1 className="fancy-h1">React Sidebar Push</h1>
    
      <article className="article float-shadow">
        <h2 className="fancy-h2">Overview:</h2>
    
        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed consequat et ante et egestas.
        Sed tristique tincidunt massa non egestas. Maecenas ultricies est sed enim laoreet, vitae
        faucibus turpis accumsan. Pellentesque maximus tortor eget metus placerat, ac tincidunt velit volutpat.
        In ornare, ipsum vel eleifend tincidunt, lorem velit pulvinar enim, quis feugiat libero tortor ut sem.
        Duis ornare a ligula ac ultricies. Nullam porttitor turpis sagittis tellus efficitur, quis interdum lectus semper.
        Phasellus auctor felis lectus, vel lacinia nulla blandit eget.</p>
      </article>
    </React.Fragment>
  );
}



export default class Navbar extends React.Component {
  state = { sidebarIsOpen: false };


  toggleSidebar = () => {
    //I'm not thrilled about reaching out to the DOM here, but I also don't
    //want to make another wrapper just for the body element. Maybe later...
    const body    = document.getElementsByTagName('body')[0];
    const overlay = document.getElementsByClassName('content-container-overlay')[0];


    if (!this.state.sidebarIsOpen) {
      this.setState({ sidebarIsOpen: true });
      body.classList.add("open");
      overlay.style.display = "block";
      //Allow sufficient time for display = "block" to take effect before
      //adding .active (in order for the transition to work);
      setTimeout(() => {overlay.classList.add('active');}, 100);
    } else {
      this.setState({ sidebarIsOpen: false });
      body.classList.remove("open");
      overlay.classList.remove('active');
      //Set it back to display: block once the transition is complete.
      setTimeout(() => { overlay.style.display = "none"; }, 350);
    }
  }


  render(){
    return (
      <React.Fragment>
        <Sidebar brand="DC" />

        <ContentContainer>
          <Overlay toggleSidebar={this.toggleSidebar} />
          <SidebarToggleButton toggleSidebar={this.toggleSidebar} />

          <RandomContent />
        </ContentContainer>
      </React.Fragment>
    );
  }
};
