import { Button, Grid, Typography, makeStyles } from '@material-ui/core';
import React from 'react';
import NavigationBar from '../components/NavigationBar.js';
import '../styles/Home.css';
import Footer from '../components/Footer.js';
import Header from '../images/home_header.jpg';

const useStyles = makeStyles((theme) => ({
    header: {
        position: 'relative',
    },
    bg: {
        opacity: 0.5,
        height: '100%',
        background: 'black',
    },
    content: {
        width: '100%',
        position: 'absolute',
        top: '20%',
        textAlign: 'center',
    },
    name: {
        fontWeight: 'bolder',
        color: 'white',
    },
    joinus: {
        background: '#fc9fb6',
        fontSize: '25px',
        color: 'white',
        fontWeight: 'bold',
        width: '30%',
        marginTop: '5%',
        borderRadius: '0px',
        borderColor: 'white',
        borderStyle: 'solid',
        borderWidth: '2px',
        "&:hover": {
            background: '#fc9fb6',
        }
    }
}));

const Home = () => {
    const classes = useStyles();
    return (
        <>
        <NavigationBar/>
        <div className={classes.header}>
            <img width='100%' src={Header} className={classes.bg}/>
            <div className={classes.content}>
                <Typography variant="h1" className={classes.name}>
                    Becoming<br/>Businesswomen
                </Typography>
                <Button className={classes.joinus}>Join Us</Button>
            </div>
        </div>
        <Grid container direction="column" style={{height: '500px'}}>
            <Grid item container>
                <Grid item xs={0} sm={2} />
                <Grid item xs={12} sm={8} >
                    Welcome, B.B.
                </Grid>
                <Grid item xs={0} sm={2} />
            </Grid>
        </Grid>
        <Footer/>
        </>
    )
}

export default Home
