import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Card, CardContent, CardMedia, Typography, CardHeader } from '@material-ui/core';
import LinkedInIcon from '@material-ui/icons/LinkedIn';

const useStyles = makeStyles({
  root: {
    paddingLeft: '5px',
    paddingTop: '5px',
    paddingRight: '5px',
  },
  header: {
    margin: 0,
    padding: 0,
    textAlign: 'center',
  },
  displayText: {
    visibility: 'hidden',
  },
  cardHover: {
    position: "relative",
    overflow: 'hidden',
    '&:hover': {
      backgroundColor: '#f5f5f5',
      '& p': {
        visibility: 'visible',
      }
    }
  }
});

export default function MediaCard(props) {
  const classes = useStyles();
  const {name, designation, linkedin, image}=props;
  return (
    <Card 
      style = {{
        backgroundImage: `${image}`,
      }}
      >
      
      <CardHeader
        className = {classes.header}
        style = {{
          backgroundImage: `${image}`,
        }}
        title = {
          <Typography component="h5">
            {name}
          </Typography>
        }
        subheader = {designation}
        //action = {
          //<IconButton aria-label="connect" onClick={linkedin}>
            //<LinkedInIcon />
          //</IconButton>
        //}
      />
      <CardContent className={classes.cardHover}>
        <Typography className={classes.displayText}>
          title</Typography>
          </CardContent>
    </Card>
  );
}
