import { Button, Grid, makeStyles, Paper, Typography } from '@material-ui/core';
import React, { useState } from 'react';

const useStyles = makeStyles ((theme) => ({

}));

const Tools = (props) => {
    const {pictureFirst, image, desc, buttonText} = props;
    const [picFirst, setState] = useState(true); 
    if(pictureFirst==="true")
        setState(true);
    else
        setState(false);
    const classes = useStyles();
    return (
        <>
        <Grid container style={picFirst ? {visibility: 'display'} : {visibility: 'none'}}>
            <Grid item sm={2}>
                <img src={image}/>
            </Grid>    
            <Grid item sm={2}>
                <Paper square>
                    <Typography>{desc}</Typography>
                    <Button>{buttonText}</Button>
                </Paper>
            </Grid>
        </Grid>
        <Grid container style={picFirst ? {visibility: 'none'} : {visibility: 'display'}}>  
            <Grid item sm={2}>
                <Paper square>
                    <Typography>{desc}</Typography>
                    <Button>{buttonText}</Button>
                </Paper>
            </Grid>
            <Grid item sm={2}>
                <img src={image}/>
            </Grid>  
        </Grid>
        </>
    )
}

export default Tools
