import React from 'react';
import { makeStyles, Card, CardContent, CardMedia } from '@material-ui/core';
import Emily from '../../images/sample_image.jpg';

const useStyles = makeStyles({
  root: {
    maxWidth: 220,
    height: 220,
    background: `url(${Emily})`,
    backgroundSize: '100%',
  },
  text: {
    color: 'white',
    visibility: 'hidden',
  },
  content: {
    height: 220,
    position: 'relative',
    overflow: 'hidden',
    visibility: 'hidden',
    "&:hover": {
      backgroundColor: 'blue',
      '& p': {
        visibility: 'visible',
      }
    }
  },
});

export default function MediaCard(props) {
  const classes = useStyles();
  const {name, designation, image}=props;
  return (
    <Card className={classes.root}>
      <CardContent className={classes.content}>
        <Typography variant="h4" gutterBottom className={classes.text}>hihhjnk</Typography>
      </CardContent>
    </Card>
  );
}
